import { useState } from "react";
import NoteContext from "./NoteContext";

const NoteState = (props) => {
    const host = "http://localhost:5000"
    const noteInitial = []
    const [notes, setNotes] = useState(noteInitial)

    //fetch all notes
    const getNotes = async () => {
        const response = await fetch(`${host}/api/notes/fetchallnode`, {
            method: "GET",
            headers: {
                "Content-type": "application/json",
                "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjM4NGQ0Njg1MTU5ZGIxZDE0YTY0NDFiIn0sImlhdCI6MTY2OTY1MjIzOX0.5WfM9VS0Mu9VZy4i6q7RZEQP8vhklxqxXhJuA1IdPZs"
            },
        });
        const json = await response.json()
        setNotes(json)
    }

    //add note
    const addNote = async (title, description, tag) => {
        const response = await fetch(`${host}/api/notes/addnote`, {
            method: "POST",
            headers: {
                "Content-type": "application/json",
                "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjM4NGQ0Njg1MTU5ZGIxZDE0YTY0NDFiIn0sImlhdCI6MTY2OTY1MjIzOX0.5WfM9VS0Mu9VZy4i6q7RZEQP8vhklxqxXhJuA1IdPZs"
            },
            body: JSON.stringify({ title, description, tag })
        });
        const json = await response.json()
        console.log(json)
        const note = {
            "_id": "6385a4f6264fa2ffb2acd3ab54de",
            "user": "6384d4685159db1d14a6441b",
            "title": title,
            "description": description,
            "tag": tag,
            "date": "2022-11-29T06:19:14.634Z",
            "__v": 0
        };
        setNotes(notes.concat(note))
    }
    //delete note
    const deleteNote = async (id) => {
            const response = await fetch(`${host}/api/notes/deletenote/${id}`, {
                method: "DELETE",
                headers: {
                    "Content-type": "application/json",
                    "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjM4NGQ0Njg1MTU5ZGIxZDE0YTY0NDFiIn0sImlhdCI6MTY2OTY1MjIzOX0.5WfM9VS0Mu9VZy4i6q7RZEQP8vhklxqxXhJuA1IdPZs"
                },
                
            });
            const json = await response.json()
        console.log(json)
        const newNotes = notes.filter((note) => { return note._id !== id })
        setNotes(newNotes)
    }

    //edit note
    const editNote = async (id, title, description, tag) => {
        const response = await fetch(`${host}/api/notes/updatenote/${id}`, {
            method: "PUT",
            headers: {
                "Content-type": "application/json",
                "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjM4NGQ0Njg1MTU5ZGIxZDE0YTY0NDFiIn0sImlhdCI6MTY2OTY1MjIzOX0.5WfM9VS0Mu9VZy4i6q7RZEQP8vhklxqxXhJuA1IdPZs"
            },
            body: JSON.stringify({ title, description, tag })
        });
        const json = await response.json()
        console.log(json)

        let newNotes = JSON.parse(JSON.stringify(notes))
        for (let index = 0; index < newNotes.length; index++) {
            const element = newNotes[index];
            if (element._id === id) {
                newNotes[index].title = title;
                newNotes[index].description = description;
                newNotes[index].tag = tag
                break
            }

        }
        setNotes(newNotes);
    }
    return (
        <NoteContext.Provider value={{ notes, getNotes, addNote, deleteNote, editNote }}>
            {props.children}
        </NoteContext.Provider>
    )

}

export default NoteState
