import React, { useContext, useState } from 'react'
import NoteContext from '../context/notes/NoteContext';

const Addnote = () => {
    const context = useContext(NoteContext);
    const [note, setNote] = useState({title:"", description:"", tag:"defult"})
    const {addNote } = context
    const handleClic =(e)=>{
        e.preventDefault();
        addNote(note.title, note.description, note.tag);
    }
    const onChange =(e)=>{
        setNote({...note, [e.target.name]: e.target.value})
    }
    return (
        <div className="container my-3">
            <h2>Add a note</h2>
            <form className='my-3'>
                <div className="mb-3">
                    <label htmlFor="title" className="form-label">title</label>
                    <input type="text" className="form-control" onChange={onChange} id="title" name="title" aria-describedby="emailHelp" />
                    
                </div>
                <div className="mb-3">
                    <label htmlFor="description" className="form-label">Description</label>
                    <input type="text" className="form-control" onChange={onChange} id="description" name="description" />
                </div>
                <div className="mb-3 form-check">
                    <input type="checkbox" className="form-check-input" id="exampleCheck1" />
                    <label className="form-check-label" htmlFor="exampleCheck1">Check me out</label>
                </div>
                <button type="submit" className="btn btn-primary" onClick={handleClic}>Submit</button>
            </form>
        </div>
    )
}

export default Addnote
